import React, { useState } from 'react';
import axios from 'axios';

const EntityForm = ({ onCreateOrUpdate }) => {
  const [name, setName] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Create or update entity
    axios.post('http://localhost:8080/entities', { name })
      .then(response => {
        onCreateOrUpdate(response.data); // Pass newly created/updated entity to parent component
        setName(''); // Reset form input
      })
      .catch(error => {
        console.error('Error creating/updating entity:', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
      </label>
      <button type="submit">Save</button>
    </form>
  );
};

export default EntityForm;
