import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EntityList = () => {
  const [entities, setEntities] = useState([]);

  useEffect(() => {
    // Fetch entities from backend API when component mounts
    axios.get('http://localhost:8080/entities')
      .then(response => {
        setEntities(response.data);
      })
      .catch(error => {
        console.error('Error fetching entities:', error);
      });
  }, []); // Empty dependency array to run effect only once when component mounts

  const handleEdit = async (id) => {
    try {
      // Fetch the entity with the specified ID from the backend API
      const response = await axios.get(`http://localhost:8080/entities/${id}`);
      const editedEntity = response.data;

      // Handle edit action (e.g., navigate to edit page or open modal)
      console.log('Edit entity with ID:', id, 'Data:', editedEntity);
    } catch (error) {
      console.error('Error fetching entity for edit:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      // Send DELETE request to backend API to delete entity by ID
      await axios.delete(`http://localhost:8080/entities/${id}`);
      // Remove the deleted entity from the list
      setEntities(prevEntities => prevEntities.filter(entity => entity.id !== id));
      console.log('Entity deleted successfully');
    } catch (error) {
      console.error('Error deleting entity:', error);
    }
  };

  return (
    <div>
      <h2>Entities</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Actions</th> {/* Add a column for actions */}
          </tr>
        </thead>
        <tbody>
          {entities.map(entity => (
            <tr key={entity.id}>
              <td>{entity.id}</td>
              <td>{entity.name}</td>
              <td>
                <button onClick={() => handleEdit(entity.id)}>Edit</button> {/* Edit button */}
                <button onClick={() => handleDelete(entity.id)}>Delete</button> {/* Delete button */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EntityList;
