import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EntityForm = ({ onCreate }) => {
  const [name, setName] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Create new entity
      const response = await axios.post('http://localhost:8080/entities', { name });
      const newEntity = response.data;
      onCreate(newEntity); // Pass newly created entity to parent component
      setName(''); // Reset form input
    } catch (error) {
      console.error('Error creating entity:', error);
    }
  };

  useEffect(() => {
    // Add additional side effects here if needed
  }, [name]); // Trigger effect when 'name' state changes

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
      </label>
      <button type="submit">Save</button>
    </form>
  );
};

export default EntityForm;
