import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaEdit, FaTrash } from 'react-icons/fa';
import "./EntityList.css"

const EntityList = () => {
  const [entities, setEntities] = useState([]);
  const [editingEntityId, setEditingEntityId] = useState(null);
  const [editedEntityName, setEditedEntityName] = useState('');

  useEffect(() => {
    // Fetch entities from backend API when component mounts
    axios.get('http://localhost:8080/entities')
      .then(response => {
        setEntities(response.data);
      })
      .catch(error => {
        console.error('Error fetching entities:', error);
      });
  });

  const handleEdit = (id, name) => {
    setEditingEntityId(id);
    setEditedEntityName(name);
  };

  const handleSave = async () => {
    try {
      // Send PUT request to update entity name on the backend
      await axios.put(`http://localhost:8080/entities/${editingEntityId}`, {
        name: editedEntityName
      });
      // Refresh entities list after update
      axios.get('http://localhost:8080/entities')
        .then(response => {
          setEntities(response.data);
        })
        .catch(error => {
          console.error('Error fetching entities:', error);
        });
      console.log('Entity updated successfully');
      // Reset editing state
      setEditingEntityId(null);
      setEditedEntityName('');
    } catch (error) {
      console.error('Error updating entity:', error);
    }
  };

  const handleCancelEdit = () => {
    // Reset editing state
    setEditingEntityId(null);
    setEditedEntityName('');
  };

  const handleDelete = async (id) => {
    try {
      // Send DELETE request to backend API to delete entity by ID
      await axios.delete(`http://localhost:8080/entities/${id}`);
      // Remove the deleted entity from the list
      setEntities(prevEntities => prevEntities.filter(entity => entity.id !== id));
      console.log('Entity deleted successfully');
    } catch (error) {
      console.error('Error deleting entity:', error);
    }
  };

  return (
    <div>
      <h2>Wpisy</h2>
      <table className="entity-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Imie</th>
            <th>Akcje</th>
          </tr>
        </thead>
        <tbody>
          {entities.map(entity => (
            <tr key={entity.id}>
              <td>{entity.id}</td>
              <td>
                {editingEntityId === entity.id ? (
                  <input
                    type="text"
                    value={editedEntityName}
                    onChange={e => setEditedEntityName(e.target.value)}
                  />
                ) : (
                  entity.name
                )}
              </td>
              <td>
                {editingEntityId === entity.id ? (
                  <div>
                    <button className="save-button" onClick={handleSave}>Save</button>
                    <button className="cancel-button" onClick={handleCancelEdit}>Cancel</button>
                  </div>
                ) : (
                  <>
                    <button className="edit-button" onClick={() => handleEdit(entity.id, entity.name)}><FaEdit /></button>
                    <button className="delete-button" onClick={() => handleDelete(entity.id)}><FaTrash /></button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EntityList;
