import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import EntityList from './components/EntityList';
import EntityForm from './components/EntityForm';

function App() {
  const [entities, setEntities] = useState([]);

  useEffect(() => {
    // Fetch entities from backend API when component mounts
    axios.get('http://localhost:8080/entities')
      .then(response => {
        setEntities(response.data);
      })
      .catch(error => {
        console.error('Error fetching entities:', error);
      });
  }); 

  const handleEntityCreate = (newEntity) => {
    // Update entities list with newly created entity
    setEntities(prevEntities => [...prevEntities, newEntity]);
  };

  return (
    <div className="App">
      <h1>Welcome to My Entity Manager</h1>
      <EntityForm onCreate={handleEntityCreate} />
      <EntityList entities={entities} />
    </div>
  );
}

export default App;
