import React, { useState } from 'react';
import axios from 'axios';

const EntityForm = ({ onCreate }) => {
  const [entityData, setEntityData] = useState({
    nam: '',
    // Add additional attributes here as needed
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setEntityData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Create new entity
      const response = await axios.post('http://localhost:8080/entities', entityData);
      const newEntity = response.data;
      onCreate(newEntity); // Pass newly created entity to parent component
      setEntityData({
        name: '', // Reset form input
        // Reset additional attributes here as needed
      });
    } catch (error) {
      console.error('Błąd tworzenia wpisu:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" name="name" value={entityData.name} onChange={handleChange} />
      </label>
      {/* Add additional input fields for other attributes as needed */}
      <button type="submit">Zapisz</button>
    </form>
  );
};

export default EntityForm;
